import React from "react";

export default function Card(props){
  let badgeText
  if(props.item.openSpots === 0){
    badgeText = "SOLD OUT"
  } else if(props.item.location === "Online"){
    badgeText="ONLINE"
  }

  
  return(
    <div className="card-section">
      <div className="card">
          <img src={`../images/${props.item.coverImg}`} className="card-image"/>
          {badgeText && <div className="sold">{badgeText}</div>}
          <div> 
            <img src="./images/star 1.png" width={14}/> {props.item.stats.rating}  <span className="six">({props.item.stats.reviewCount}).{props.item.location}</span>
          </div>
          
          <p className="life">
            {props.item.title}
          </p>
          <p className="lifes">
            <span className="from">From ${props.item.price}</span> / person
          </p>

        </div>
    </div>
  )

}
